CREATE TABLE IF NOT EXISTS "public"."hospital_profession"("hospital_id" uuid NOT NULL, "profession_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "default" boolean NOT NULL DEFAULT false, PRIMARY KEY ("hospital_id","profession_id") , FOREIGN KEY ("hospital_id") REFERENCES "public"."hospital"("uid") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("profession_id") REFERENCES "public"."profession"("uid") ON UPDATE restrict ON DELETE restrict);